const data112 = [
  [1, 3, 5],
  [3, 4, 6],
];

export default function Trend() {
  const [data23,setdata23 ]= useState([]);
  const [min, setmin] = useState(0);
  const [namei, setname] = useState("");
  const [max, setmax] = useState(1);
  const [data, setdata] = useState([]);
  const [data2, setdata2] = useState([]);
  const [alldata, setdata3] = useState([]);
  const [cat, allcat] = useState([]);
  const [pref, allpref] = useState({'categories':[]});
  const [loading, setLoading] = useState(false);
  const [hotpro, sethotpro] = useState([]);
  let navigate = useNavigate();
  const [sens, setsens] = useState("");
    useEffect(() => {
      var name = sessionStorage.getItem("email") || "";
      var firstime = sessionStorage.getItem("firsttime") || "";
      setname(name);
      if(name ===""){
        navigate('/login');
      }

      fetch('http://127.0.0.1:5000/get_user_hot', {
          method: 'POST',
          headers: {
            'content-type': 'application/json',
          },
          body: JSON.stringify({
              "username":name,
          }),
        }).then((response) => {
          response.json().then((body) => {
            sethotpro(body['hot']);
            setdata23(body['tre']);
        })
      });
      
      fetch('http://127.0.0.1:5000/get_all_pref', {
                    method: 'GET',
                    headers: {
                      'content-type': 'application/json',
                    },
                  }).then((response) => {
                    response.json().then((body) => {
                      allpref(body)
                     
                      
                     
                  })
              })
      fetch('http://127.0.0.1:5000/get_all_categories', {
                    method: 'GET',
                    headers: {
                      'content-type': 'application/json',
                    },
                  }).then((response) => {
                    response.json().then((body) => {
                      allcat(body)
                     
                  })
              })
      setTimeout(() => setLoading(false), 1500);
      if(firstime === "true"){
        var boxes = document.getElementsByClassName(
          'firsttime',
        ) as HTMLCollectionOf<HTMLElement>;
        if (boxes.length > 0) {
          console.log(firstime)
          boxes[0].style.display = 'block';
        }
      }
      const handleBeforeUnload = (event) => {
        event.preventDefault();
        event.returnValue = 'Are you sure you want to leave this page?';
      };
  
      window.addEventListener('beforeunload', handleBeforeUnload);
  
      return () => {
        window.removeEventListener('beforeunload', handleBeforeUnload);
      };
      
    }, []);

    // const [isOpen, setIsOpen] = useState(false);

    // const toggleDropdown = () => {
    //   setIsOpen(!isOpen);
    // };

    const toggleic = () =>{
      
        var boxes = document.getElementsByClassName(
          'hoveric',
        ) as HTMLCollectionOf<HTMLElement>;
        if (boxes.length > 0) {
          if(boxes[0].style.display === 'block'){
            boxes[0].style.display = 'none';
            
          }
          else{
            boxes[0].style.display = 'block';
          }
          
        }
    }

  const search = () =>{
     fetch('http://127.0.0.1:5000/search', {
          method: 'POST',
          headers: {
            'content-type': 'application/json',
        },
          body: JSON.stringify({
            "name":seain
          }),
        }).then((response) => {
          response.json().then((body) => {
            
                  fetch('http://127.0.0.1:5000/get_text', {
                    method: 'POST',
                    headers: {
                      'content-type': 'application/json',
                    },
                    body: JSON.stringify({
                      "lists":body['main_list']
                    }),
                  }).then((response) => {
                    response.json().then((body) => {
                      setdata(body['original'])
                      setdata2(body['bar_chart_data'])
                      setdata3(body)
                      if(body['threshold'] === "High" && body['trend'] === "Increasing"){
                          setsens("This item is currently very popular and its appeal is growing over time. It may be a good investment opportunity to consider as it is gaining momentum in the market.")
                      }
                      else if(body['threshold'] === "High" && body['trend'] === "Decreasing"){
                          setsens("This item is currently very popular but its appeal is declining over time. While it may still be a profitable investment in the short term, it may not have long-term sustainability. It's worth considering other investment options that have a more stable or increasing trend.")
                      }
                      else if(body['threshold'] === "Moderate" && body['trend'] === "Increasing"){
                        setsens("This item is gaining in popularity and has a positive trend. While it may not have the same level of demand as more popular items, it could still be a good investment opportunity to consider as it is on the rise.")
                      }
                      else if(body['threshold'] === "Moderate" && body['trend'] === "Decreasing"){
                        setsens("This item has a moderate level of popularity but its appeal is declining over time. It may be a risky investment to consider as it may not have the same level of profitability in the future. It's worth considering other investment options that have a more stable or increasing trend.")
                      }
                      else if(body['threshold'] === "Low" && body['trend'] === "Increasing"){
                        setsens("This item is currently not very popular but its appeal is growing over time. It may be a good investment opportunity to consider as it has potential for growth in the market.")
                      }
                      else if(body['threshold'] === "Low" && body['trend'] === "Decreasing"){
                        setsens("This item is not very popular and its appeal is declining over time. It may not be a profitable investment opportunity to consider as it may not have long-term sustainability. It's worth considering other investment options that have a more stable or increasing trend.")
                      }
                      setmin(  (Math.round(Math.min(...body['original'].map(item => item.label))* 10)/10)-0.05  )
                      setmax(  (Math.round(Math.max(...body['original'].map(item => item.label))* 10)/10)+0.05  )
                      var boxes = document.getElementsByClassName(
                        'line',
                      ) as HTMLCollectionOf<HTMLElement>;
                      console.log( body['score'])
                      if (boxes.length > 0) {
                        boxes[0].style.left = body['score'];
                      }
                      // var boxes = document.getElementsByClassName(
                      //   'graphs',
                      // ) as HTMLCollectionOf<HTMLElement>;
                      if (boxes.length > 0) {
                        boxes[0].style.display = 'block';
                      }
                      boxes = document.getElementsByClassName(
                        'hotcategory',
                      ) as HTMLCollectionOf<HTMLElement>;
                      if (boxes.length > 0) {
                        boxes[0].style.display = 'none';
                      }
                      boxes = document.getElementsByClassName(
                        'levelff',
                      ) as HTMLCollectionOf<HTMLElement>;
                      if (boxes.length > 0) {
                        boxes[0].style.display = 'none';
                      }
                  })
              })
          })
        });
  }

  const [seain, seaset] = useState("");
  const xAxisTickStyle = {
    display:"none",
    fontSize: '12px', 
    transform: 'rotate(45deg)', // Rotate the ticks by -45 degrees
    // textAnchor: 'end' 
  };
  const seat = (e: React.ChangeEvent<HTMLInputElement>) =>{
    seaset(e.target.value);
  }

  const formatYAxisTick = (value) => {
    return Math.round(value * 100) / 100;
  };


  // Set up state to store the checked options
  // const [checkedOptions, setCheckedOptions] = useState<string[]>([]);

  // Handle checkbox change
  // const handleCheckboxChange = (event: any) => {
  //   const { value, checked } = event.target;

  //   setCheckedOptions((prevCheckedOptions) => {
  //     if (checked) {
  //       // Add the checked option to the state
  //       return [...prevCheckedOptions, value];
  //     } else {
  //       // Remove the unchecked option from the state
  //       return prevCheckedOptions.filter((option) => option !== value);
  //     }
  //   });
  // };

  // Handle form submission
  // async function handleSubmit () {
  //   var name = sessionStorage.getItem("email") || "";
  //   await fetch('http://127.0.0.1:5000/prefset', {
  //                   method: 'POST',
  //                   headers: {
  //                     'content-type': 'application/json'
  //                   },
  //                   body: JSON.stringify({
  //                       "username":namei,
  //                       "pref":checkedOptions,
  //                   }),
  //                 }).then((response) => {
  //                   response.json().then((body) => {
  //                      if( body['message'] === 'Done'){
  //                       var boxes = document.getElementsByClassName(
  //                         'firsttime',
  //                       ) as HTMLCollectionOf<HTMLElement>;
  //                       if (boxes.length > 0) {
         
  //                         boxes[0].style.display = 'none';
  //                       }
  //                           navigate('/trend');
  //                           sessionStorage.setItem("firsttime", "false");
  //                           // toast.success(body['error']);
  //                      }
  //                      else{
  //                           console.error("There is Some Error!")
  //                      }
  //                 })
  //               });
  //               fetch('http://127.0.0.1:5000/get_user_hot', {
  //         method: 'POST',
  //         headers: {
  //           'content-type': 'application/json',
  //         },
  //         body: JSON.stringify({
  //             "username":name,
  //         }),
  //       }).then((response) => {
  //         response.json().then((body) => {
  //           sethotpro(body['hot']);
  //           setdata23(body['tre']);
  //       })
  //     });
  // };

  const handlegraphs = (is:any) => {

    fetch('http://127.0.0.1:5000/search', {
          method: 'POST',
          headers: {
            'content-type': 'application/json',
        },
          body: JSON.stringify({
            "name":is
          }),
        }).then((response) => {
          response.json().then((body) => {
            
                  fetch('http://127.0.0.1:5000/get_text', {
                    method: 'POST',
                    headers: {
                      'content-type': 'application/json',
                    },
                    body: JSON.stringify({
                      "lists":body['main_list']
                    }),
                  }).then((response) => {
                    response.json().then((body) => {
                      setdata(body['original'])
                      setdata2(body['bar_chart_data'])
                      setdata3(body)
                      if(body['threshold'] === "High" && body['trend'] === "Increasing"){
                        setsens("This item is currently very popular and its appeal is growing over time. It may be a good investment opportunity to consider as it is gaining momentum in the market.")
                    }
                    else if(body['threshold'] === "High" && body['trend'] === "Decreasing"){
                        setsens("This item is currently very popular but its appeal is declining over time. While it may still be a profitable investment in the short term, it may not have long-term sustainability. It's worth considering other investment options that have a more stable or increasing trend.")
                    }
                    else if(body['threshold'] === "Moderate" && body['trend'] === "Increasing"){
                      setsens("This item is gaining in popularity and has a positive trend. While it may not have the same level of demand as more popular items, it could still be a good investment opportunity to consider as it is on the rise.")
                    }
                    else if(body['threshold'] === "Moderate" && body['trend'] === "Decreasing"){
                      setsens("This item has a moderate level of popularity but its appeal is declining over time. It may be a risky investment to consider as it may not have the same level of profitability in the future. It's worth considering other investment options that have a more stable or increasing trend.")
                    }
                    else if(body['threshold'] === "Low" && body['trend'] === "Increasing"){
                      setsens("This item is currently not very popular but its appeal is growing over time. It may be a good investment opportunity to consider as it has potential for growth in the market.")
                    }
                    else if(body['threshold'] === "Low" && body['trend'] === "Decreasing"){
                      setsens("This item is not very popular and its appeal is declining over time. It may not be a profitable investment opportunity to consider as it may not have long-term sustainability. It's worth considering other investment options that have a more stable or increasing trend.")
                    }
                      var boxes = document.getElementsByClassName(
                        'line',
                      ) as HTMLCollectionOf<HTMLElement>;
                      console.log( body['score'])
                      if (boxes.length > 0) {
                        boxes[0].style.left = body['score'];
                      }
                      setmin(  (Math.round(Math.min(...body['original'].map(item => item.label))* 10)/10)-0.05  )
                      setmax(  (Math.round(Math.max(...body['original'].map(item => item.label))* 10)/10)+0.05  )
                      // var boxes = document.getElementsByClassName(
                      //   'graphs',
                      // ) as HTMLCollectionOf<HTMLElement>;
                      if (boxes.length > 0) {
                        boxes[0].style.display = 'block';
                      }
                      boxes = document.getElementsByClassName(
                        'hotcategory',
                      ) as HTMLCollectionOf<HTMLElement>;
                      if (boxes.length > 0) {
                        boxes[0].style.display = 'none';
                      }
                      boxes = document.getElementsByClassName(
                        'levelff',
                      ) as HTMLCollectionOf<HTMLElement>;
                      if (boxes.length > 0) {
                        boxes[0].style.display = 'none';
                      }
                      
                  })
              })
          })
        });
  }

  const toggleico = () =>{
      
    var boxes = document.getElementsByClassName(
      'hoveric',
    ) as HTMLCollectionOf<HTMLElement>;
    if (boxes.length > 0) {

        boxes[0].style.display = 'block';
        
      
      
    }
}
const toggleicp = () =>{
      
  var boxes = document.getElementsByClassName(
    'hoveric',
  ) as HTMLCollectionOf<HTMLElement>;
  if (boxes.length > 0) {

      boxes[0].style.display = 'none';
      
    
    
  }
}




  return (
    
    <div className='home'>
      {loading?<SplashScreen/>:(
      <div>
        <div className='login'>
      </div>
      {/*
      <div className='firsttime'>
              <img className='a2' src={doc3} /> 
            <img className='a1' src={doc2} />
            <h1 className='a5'>Please select your Prefered Categories</h1>
          <form className='forms' onSubmit={handleSubmit}>
            {pref['categories'].map((option, index) => (
              <div key={index}>
                <input
                  type="checkbox"
                  id={option}
                  value={option}
                  onChange={handleCheckboxChange}
                />
                <label className='labelf' htmlFor={option}>{option}</label>
              </div>
            ))}
            <button onClick={()=>{handleSubmit()}} className='subpref' >Submit</button>
          </form>
           </div>
          */}
     
      <div className='navs'>
      <p className='labelc'>Product Scout</p>

        <div className='nav-cont' >
            <div>
                <div className="search-containerz">
                    <i className="search-icon fas fa-search"></i>
                    <input
                        className="search-input"
                        type="text"
                        placeholder="Article name or keywords..."
                        onChange={seat}
                        value={seain}
                    />
                </div>
                <div>
                {/* <div className="dropdown">
                    <button onClick={toggleDropdown} className="dropdown-btn">
                        All Categories <i className="dropicon fas fa-chevron-down"></i>
                    </button>
                    {isOpen && (
                        <div className="dropdown-content">
                          
                          {
                          cat['categories'].map((number) => (
                            <li key={number}>
                              <a >{number}</a>
                            </li>
                          ))
                        } 

                        </div>
                    )}
                    </div> */}
                    
                </div>
                <button className='Searchbuttz' onClick={search}>
                        Search
                </button>

                <button className='catz' onClick={() => { navigate("/select-category") }}>
                        Home
                </button>

                <button className='logoutz' onClick={() => { navigate("/") }}>
                        Logout
                </button>
                <button className='trendz' onClick={() => { navigate("/hotcategoreis") }}>
                        Hot Products
                </button>

                <button className='calcz' onClick={() => { navigate("/calculator") }}>
                        Profit Calculator
                </button>
            </div>
          
          
        </div>
      </div>
      <div className='hotcategory'>
        <h1 className='hch'>Current Hot Products based on your interest</h1>
        <p className='hhp'>Click to see predict future trend of these products</p>
      
        {hotpro.map((category, index) => (
          <div className='hotproduct' onClick={()=>{handlegraphs(Object.keys(category)[0])}} >
             {Object.keys(category)[0]}
          </div>
        ))}


      </div>
      <ResponsiveContainer className='levelff'width="40%" height="80%">
        <ComposedChart
          width={500}
          height={300}
          data={data23}
          margin={{
            top: 5,
            right: 30,
            left: 20,
            bottom: 5,
          }}
        >
       
          {/* <XAxis dataKey="name" />
          <YAxis /> */}
          <Tooltip />
          <Legend verticalAlign="bottom" wrapperStyle={{ lineHeight: '40px' }}  />
          {/* <ReferenceLine y={0} stroke="#000" /> */}
          <Brush dataKey="name" height={30} stroke="#f2065f" y={460}/>
          <Bar dataKey="market_trend" fill="#f2003d" />
          <Line type="monotone" dataKey="running_average" stroke="#030637" />
          <text x={300} y={555} fontSize={13} fontWeight={600} textAnchor="middle" fill="#030637">
          GRAPH SHOWING MARKET SALES OVERALL TREND IN ALL CATEGORIES FOR PAST 30 DAYS
              </text>
        </ComposedChart>
      </ResponsiveContainer>
      <div className='graphs'>
          <ResponsiveContainer className='chart2' width="45%" height="5%">
            
            <BarChart width={150} height={200} data={data2}>
            <CartesianGrid strokeDasharray="3 3" />
              <XAxis tick={xAxisTickStyle} dataKey="name" />
              <YAxis />
              <Bar dataKey="value" fill="#f2003d" />
              <text x={450} y={380} fontSize={20} textAnchor="middle" fill="#030637">
                Relevant Categories
              </text>
              <Tooltip />
            </BarChart>
          </ResponsiveContainer>
          <ResponsiveContainer className='chart' width="45%" height="40%">
            <LineChart
            width={500}
            height={300}
            data={data}
            margin={{
                top: 20,
                right: 50,
                left: 20,
                bottom: 5,
            }}
            >
    
            <XAxis  tick={{ stroke: '#030637', fontSize: 12  }} axisLine={{ stroke: '#030637' }} dataKey="x" />
            <YAxis tick={{ stroke: '#030637', fontSize: 12  }} axisLine={{ stroke: '#030637' }} tickFormatter={formatYAxisTick} domain={[min,max]}/>
            <Tooltip />
            <Legend />
            <Line type="monotone" dataKey="trend" stroke="#f2003d" strokeWidth={2}   /> 
            {/* strokeDasharray="5 5"  */}
            </LineChart>
        </ResponsiveContainer>
        
        <div className='Stats'>
            <p><span>Max Price:</span> "Rs. {alldata['max_price']}"</p>
            &nbsp; &nbsp; &nbsp; &nbsp;
            <p><span>Min Price:</span> "Rs. {alldata['min_price']}"</p>
            &nbsp; &nbsp; &nbsp; &nbsp;
            <p><span>Mean Price:</span> "Rs. {alldata['mean_price']}"</p>
            &nbsp; &nbsp; &nbsp; &nbsp;
            <p><span>Popularity:</span> {alldata['threshold']}</p>
            &nbsp; &nbsp; &nbsp; &nbsp;
            <p><span>Trend:</span> {alldata['trend']}</p>
            &nbsp; &nbsp; &nbsp; &nbsp;
            <p className='rotateic' onMouseOver={toggleico} onMouseLeave={toggleicp} onClick={toggleic}><span>Suggestions</span></p>
            <div className='hoveric'  >
                <p>{sens}</p>
                
